import React, { useState } from "react";
import { useEffect } from "react";

const Game = () => {
  const words = ['php','java','python','c++','qbasic','javascript','css','html'];
  
  return (
    <>
      <div className="bg-emerald-300 h-[85vh] flex justify-center items-center">
        <div className="bg-emerald-500 h-[45vh] w-[50%] rounded-md shadow-lg shadow-slate-600/50 flex flex-col justify-center items-center gap-6">
          
            <h3 className="text-xl font-bold">Guess the word: {newWord}</h3>
            <h3 className="text-xl font-bold"> Wrong. Please guess again: {randomWord}</h3>
            <h3 className="text-xl font-bold"> Nice!! correct Answer </h3>

          
            <input className="bg-slate-200 h-12 rounded-3xl outline-0 px-5"></input>
          
          <button
            onClick={onSubmit}
            className="bg-white h-12 min-w-20 text-base font-medium text-red-600"
          >
            {buttontext}
          </button>
        </div>
      </div>
    </>
  );
};

export default Game;
