import React, { useState } from "react";

const Game = () => {
  const words = [
    "java",
    "python",
    "react",
    "c++",
    "php",
    "css",
    "html",
    "javascript",
    "ecma",
    "c#",
    "qbasic",
  ];
  let [show, setShow] = useState(false);
  let [buttonWord, setButton] = useState("START");
  let [randomWord, setRandom] = useState("");
  let [word, setWord] = useState("");
  let [head,setHead] = useState("Guess the Word: ");
  let [inputWord,setInput] = useState("");
  const onSubmit = () => {
    if (buttonWord === "START") {
      setShow(true);
      setButton("GUESS");
      setRandom(() => {
        let i = Math.floor(Math.random() * words.length);
        let str = words[i].split("");
        setWord(str.join(""));
        i = str.length - 1;
        for (i; i > 0; i--) {
          let j = Math.floor(Math.random() * (i + 1));
          let temp = str[i];
          str[i] = str[j];
          str[j] = temp;
        }
        return str;
      });
    }
    else{
        console.log(word);
        if(word===randomWord)
        {
            setHead("Good");
        }
        else console.log("Bad");
        setButton("START");
        //setShow(false);
    }
  };

  

  return (
    <>
      <div className="bg-emerald-300 h-[85vh] flex justify-center items-center">
        <div className="bg-emerald-500 h-[45vh] w-[50%] rounded-md shadow-lg shadow-slate-600/50 flex flex-col justify-center items-center gap-6">
          {show && (
            <h3 className="text-xl font-bold">
               {head}{randomWord}
            </h3>
          )}
          {show && (
            <input onChange={inputEvent} value = {inputWord} className="bg-slate-200 h-12 rounded-3xl outline-0 px-5"></input>
          )}
          <button
            onClick={onSubmit}
            className="bg-white h-12 w-20 text-base font-medium text-red-600"
          >
            {buttonWord}
          </button>
        </div>
      </div>
    </>
  );
};

export default Game;
