import React, { useState } from "react";

const Game = () => {
  const words = ['php','java','python','c++','qbasic','javascript','css','html'];
  const [buttontext, setButton] = useState("CLICK HERE TO START");
  const buttonTextWords = ["GUESS", "START"];
  const [i, seti] = useState(0);
  //const [show, setShow] = useState(false);
  //const [inputShow, setinputShow] = useState(false);
  const [randomWord,setRandom] = useState("");
  const [match,setMatch] = useState("false");
  const [newWord,setNew] = useState("");

  const randomize = () =>{
    const i = Math.floor(Math.random()*(words.length));
    
    setNew(words[i]);

    let str = newWord.split("");

    for(let idx = 1 ; idx<str.length ; idx++)
    {
      let j = Math.floor(Math.random()*(idx+1));
      let temp = str[idx];
      str[idx] = str[j];
      str[j] = temp;
    }

    setRandom(str.join(""));
    console.log(randomWord);
  }

  const onSubmit = () => {
    if (i === 0 || i===3) {
      //setShow(true);
      //setinputShow(true);
      seti(1);
      setButton(buttonTextWords[0]);
      randomize();
    }
  };
  return (
    <>
      <div className="bg-emerald-300 h-[85vh] flex justify-center items-center">
        <div className="bg-emerald-500 h-[45vh] w-[50%] rounded-md shadow-lg shadow-slate-600/50 flex flex-col justify-center items-center gap-6">
          {()=>{
            if(i===1) return <h3 className="text-xl font-bold">Guess the word: {randomWord}</h3>;
            else if(i===2) return <h3 className="text-xl font-bold"> Wrong. Please guess again: {randomWord}</h3>;
            else return <h3 className="text-xl font-bold"> Nice!! correct Answer </h3>;
          }}
          {()=>{
            if(i===1 || i===2) return <input className="bg-slate-200 h-12 rounded-3xl outline-0 px-5"></input>;
          }}
          <button
            onClick={onSubmit}
            className="bg-white h-12 min-w-20 text-base font-medium text-red-600"
          >
            {buttontext}
          </button>
        </div>
      </div>
    </>
  );
};

export default Game;
