import React, { useState } from "react";

const Game = () => {
    let words = ['java','python','react','c++','php','css','html','javascript','ecma','c#','qbasic'];
    let [show,setShow] = useState(false);

    const guess = () => {
        setShow(true);
    };
  return (
    <>
      <div className="bg-emerald-300 h-[85vh] flex justify-center items-center">
        <div className="bg-emerald-500 h-[45vh] w-[50%] rounded-md shadow-lg shadow-slate-600/50 flex flex-col justify-center items-center gap-6">
            {show && <h3 className="text-xl font-bold"> Guess the Word:  </h3>}
            {show && <input className="bg-slate-200 h-12 rounded-3xl outline-0 px-5"></input>}
            <button onClick={guess} className="bg-white h-12 w-20 text-base font-medium text-red-600"> GUESS </button>
        </div>
      </div>
    </>
  );
};

export default Game;
